import csv
import os
from datetime import datetime
from pathlib import Path

def main():
    base_path = Path(__file__).resolve().parent
    score_path = base_path / "score.txt"
    csv_path = base_path / "leaderboard.csv"
    md_path = base_path / "leaderboard.md"

    # 1. Read the score generated by evaluate.py
    if not score_path.exists():
        print("❌ score.txt not found. evaluate.py likely failed.")
        return

    score = score_path.read_text().strip()
    team = os.getenv("PARTICIPANT", "Unknown")
    date = datetime.now().strftime("%Y-%m-%d")

    # 2. Update CSV
    file_exists = csv_path.exists()
    with open(csv_path, "a", newline='', encoding='utf-8') as f:
        writer = csv.writer(f)
        if not file_exists or os.stat(csv_path).st_size == 0:
            writer.writerow(["team", "score", "date"])
        writer.writerow([team, score, date])
    
    # 3. Sort & Render Markdown
    rows = []
    with open(csv_path, "r", encoding='utf-8') as f:
        reader = csv.DictReader(f)
        for row in reader:
            try:
                row['score'] = float(row['score'])
                rows.append(row)
            except: continue
    
    rows.sort(key=lambda x: x['score'], reverse=True)
    
    md_content = "# Leaderboard\n\n| Rank | Team | Score | Date |\n|---|---|---|---|\n"
    for i, r in enumerate(rows, 1):
        md_content += f"| {i} | {r['team']} | {r['score']:.4f} | {r['date']} |\n"
        
    md_path.write_text(md_content, encoding='utf-8')
    print("✅ Files updated successfully.")

if __name__ == "__main__":
    main()
